import React from 'react';
import './VideoPlayer.css';

// Extract YouTube video ID from various URL formats
const extractVideoId = (videoIdOrUrl) => {
    if (!videoIdOrUrl) return null;

    // If it's already an 11-character ID, return it
    if (videoIdOrUrl.length === 11 && /^[a-zA-Z0-9_-]{11}$/.test(videoIdOrUrl)) {
        return videoIdOrUrl;
    }

    // Try to extract from YouTube URL
    const patterns = [
        /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([a-zA-Z0-9_-]{11})/,
        /youtube\.com\/.*[?&]v=([a-zA-Z0-9_-]{11})/,
    ];

    for (const pattern of patterns) {
        const match = videoIdOrUrl.match(pattern);
        if (match && match[1]) {
            return match[1];
        }
    }

    return null;
};

const VideoPlayer = ({ videoId, videoUrl, courseName }) => {
    // Use videoUrl if provided, otherwise use videoId
    const source = videoUrl || videoId;
    const extractedId = extractVideoId(source);

    if (!extractedId) {
        return (
            <div className="video-player-error">
                <p>Invalid video URL. Please check the video link.</p>
                {source && (
                    <p style={{ fontSize: '0.9rem', marginTop: '0.5rem', color: '#666' }}>
                        Original URL: {source}
                    </p>
                )}
            </div>
        );
    }

    return (
        <div className="video-player-wrapper">
            {courseName && (
                <div className="video-player-header">
                    <h3 className="video-player-course-name">{courseName}</h3>
                </div>
            )}
            <div className="video-player-container">
                <iframe
                    className="video-iframe"
                    src={`https://www.youtube.com/embed/${extractedId}?rel=0&modestbranding=1&enablejsapi=1`}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                    allowFullScreen
                ></iframe>
            </div>
            {videoUrl && (
                <div className="video-player-footer">
                    <a 
                        href={videoUrl} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="video-player-link"
                    >
                        Watch on YouTube â†—
                    </a>
                </div>
            )}
        </div>
    );
};

export default VideoPlayer;
