import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getCourses, addYouTubePlaylist } from '../api';
import './CourseCatalog.css';

const CourseCatalog = () => {
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showAddForm, setShowAddForm] = useState(false);
    const [playlistUrl, setPlaylistUrl] = useState('');
    const [adding, setAdding] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        fetchCourses();
    }, []);

    const fetchCourses = async () => {
        try {
            setLoading(true);
            const data = await getCourses();
            setCourses(data);
            setError('');
        } catch (err) {
            console.error('Error fetching courses:', err);
            setError('Failed to load courses. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleAddPlaylist = async (e) => {
        e.preventDefault();
        if (!playlistUrl.trim()) {
            setError('Please enter a valid YouTube playlist URL');
            return;
        }

        try {
            setAdding(true);
            setError('');
            await addYouTubePlaylist(playlistUrl);
            setPlaylistUrl('');
            setShowAddForm(false);
            // Refresh the course list
            await fetchCourses();
        } catch (err) {
            console.error('Error adding playlist:', err);
            setError(err.response?.data?.detail || 'Failed to add playlist. Please check the URL and try again.');
        } finally {
            setAdding(false);
        }
    };

    const handleCourseClick = (courseId) => {
        navigate(`/course/${courseId}`);
    };

    const extractPlaylistId = (playlistUrl) => {
        if (!playlistUrl) return null;
        const match = playlistUrl.match(/[?&]list=([a-zA-Z0-9_-]+)/);
        return match ? match[1] : null;
    };

    const getPlaylistThumbnail = (playlistUrl) => {
        const playlistId = extractPlaylistId(playlistUrl);
        if (playlistId) {
            // Try to get thumbnail from first video in playlist, or use default
            return `https://img.youtube.com/vi_playlist/${playlistId}/mqdefault.jpg`;
        }
        return null;
    };

    if (loading) {
        return (
            <div className="catalog-container">
                <div className="loading-spinner">Loading courses...</div>
            </div>
        );
    }

    return (
        <div className="catalog-container">
            <div className="catalog-header">
                <h1>Course Catalog</h1>
                <button 
                    className="add-course-btn"
                    onClick={() => setShowAddForm(!showAddForm)}
                >
                    {showAddForm ? 'Cancel' : '+ Add YouTube Playlist'}
                </button>
            </div>

            {showAddForm && (
                <div className="add-playlist-form">
                    <h3>Add Course from YouTube Playlist</h3>
                    <form onSubmit={handleAddPlaylist}>
                        <div className="form-group">
                            <label htmlFor="playlistUrl">YouTube Playlist URL</label>
                            <input
                                type="url"
                                id="playlistUrl"
                                value={playlistUrl}
                                onChange={(e) => setPlaylistUrl(e.target.value)}
                                placeholder="https://www.youtube.com/playlist?list=..."
                                required
                            />
                            <small className="form-hint">
                                Paste a YouTube playlist URL. All videos in the playlist will be added as course videos.
                            </small>
                        </div>
                        <button type="submit" className="submit-btn" disabled={adding}>
                            {adding ? 'Adding...' : 'Add Course'}
                        </button>
                    </form>
                </div>
            )}

            {error && <div className="error-message">{error}</div>}

            {courses.length === 0 ? (
                <div className="empty-state">
                    <p>No courses available yet.</p>
                    <p>Add a YouTube playlist to get started!</p>
                </div>
            ) : (
                <div className="courses-grid">
                    {courses.map((course) => (
                        <div
                            key={course.id}
                            className="course-card"
                            onClick={() => handleCourseClick(course.id)}
                        >
                            <div className="course-icon">
                                <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                                    <line x1="9" y1="9" x2="15" y2="9"></line>
                                    <line x1="9" y1="15" x2="15" y2="15"></line>
                                </svg>
                            </div>
                            <h3 className="course-title">{course.course_title}</h3>
                            {course.link && (
                                <div className="course-link-container">
                                    <a 
                                        href={course.link} 
                                        target="_blank" 
                                        rel="noopener noreferrer"
                                        className="course-link"
                                        onClick={(e) => e.stopPropagation()}
                                    >
                                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                            <path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6"></path>
                                            <polyline points="15 3 21 3 21 9"></polyline>
                                            <line x1="10" y1="14" x2="21" y2="3"></line>
                                        </svg>
                                        View Playlist on YouTube
                                    </a>
                                    <button
                                        className="copy-link-btn"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            navigator.clipboard.writeText(course.link);
                                            const btn = e.target;
                                            const originalText = btn.textContent;
                                            btn.textContent = 'Copied!';
                                            setTimeout(() => {
                                                btn.textContent = originalText;
                                            }, 2000);
                                        }}
                                        title="Copy playlist link"
                                    >
                                        <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                            <rect x="9" y="9" width="13" height="13" rx="2" ry="2"></rect>
                                            <path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1"></path>
                                        </svg>
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default CourseCatalog;
